// import { init } from "aos";
import { createStore } from "redux";

const initialState = {
    cart : [],
}
function reducer(state = initialState, action) {
    switch(action.type) {
        case'ADD_TO_CART': 
            return{
                ...state,
                cart:[...state]
            }
    }
}
