/* eslint-disable */
<template>
    <v-container>
      <v-data-table :headers="headers" :items="items" class="elevation-1" :items-per-page="5">
        <template v-slot:top>
          <v-toolbar flat>
            <v-toolbar-title>Item List</v-toolbar-title>
            <v-divider class="mx-4" inset vertical></v-divider>
            <v-spacer></v-spacer>
            <v-dialog v-model="dialog" max-width="500px">
              <template v-slot:activator="{ on, attrs }">
                <v-btn color="primary" dark class="mb-2" v-bind="attrs" v-on="on">
                  Add Item
                </v-btn>
              </template>
              <v-card>
                <v-card-title>
                  <span class="headline">Add New Item</span>
                </v-card-title>
                <v-card-text>
                  <v-container>
                    <v-row>
                      <v-col cols="12" sm="6" md="4">
                        <v-text-field v-model="newItem.name" label="Item Name"></v-text-field>
                      </v-col>
                      <v-col cols="12" sm="6" md="4">
                        <v-text-field v-model="newItem.price" label="Price"></v-text-field>
                      </v-col>
                      <v-col cols="12" sm="6" md="4">
                        <v-text-field v-model="newItem.quantity" label="Quantity"></v-text-field>
                      </v-col>
                    </v-row>
                  </v-container>
                </v-card-text>
                <v-card-actions>
                  <v-spacer></v-spacer>
                  <v-btn color="blue darken-1" text @click="dialog = false">Cancel</v-btn>
                  <v-btn color="blue darken-1" text @click="addItem">Save</v-btn>
                </v-card-actions>
              </v-card>
            </v-dialog>
          </v-toolbar>
        </template>
        <template v-slot:item.actions="{ item }">
          <v-icon small @click="editItem(item)">mdi-pencil</v-icon>
          <v-icon small @click="deleteItem(item)">mdi-delete</v-icon>
        </template>
      </v-data-table>
    </v-container>
  </template>
  
  <script>
  export default {
    name: "CartComponent",
    data() {
      return {
        dialog: false,
        headers: [
          { text: 'Item Name', value: 'name' },
          { text: 'Price', value: 'price' },
          { text: 'Quantity', value: 'quantity' },
          { text: 'Actions', value: 'actions', sortable: false },
        ],
        items: [
          // Sample data, replace with your actual data
          { name: 'Item 1', price: 100, quantity: 2 },
          { name: 'Item 2', price: 200, quantity: 5 },
        ],
        newItem: {
          name: '',
          price: '',
          quantity: ''
        },
        editedIndex: -1,
      };
    },
    methods: {
      addItem() {
        if (this.editedIndex > -1) {
          Object.assign(this.items[this.editedIndex], this.newItem);
          this.editedIndex = -1;
        } else {
          this.items.push({ ...this.newItem });
        }
        this.resetForm();
        this.dialog = false;
      },
      editItem(item) {
        this.editedIndex = this.items.indexOf(item);
        this.newItem = { ...item };
        this.dialog = true;
      },
      deleteItem(item) {
        const index = this.items.indexOf(item);
        confirm('Are you sure you want to delete this item?') && this.items.splice(index, 1);
      },
      resetForm() {
        this.newItem = {
          name: '',
          price: '',
          quantity: ''
        };
      }
    }
  };
  </script>
  
  <style scoped>
  .headline {
      font-weight: bold;
  }
  </style>
