// import { init } from "aos";
import { createStore } from "redux";

const initialState = {
    cart: [],
}
function cartReducer(state = initialState, action) {
    switch (action.type) {
        case 'ADD_TO_CART':
            return {
                ...state,
                cart: [...state.cart, action.payload]
            };
        default: return state;
    }
}
export const store = createStore(cartReducer);
