export default {
  name: 'ProductCatalog',
  data() {
    return {
      products: []
    };
  },
  methods: {
    async fetchProduct() {
      try {
        const respose = await fetch('http://localhost:3000/products');
        if (!respose.ok) {
          throw new Error(`Https error! status:${respose.status}`);
        }
        const product = reponse.json();
        this.products = product;
      } catch (error) {
        console.error('Error fetching products:', error);
      }
    }
  },
  created() {
    this.fetchProduct();
  }
};
