// store/index.js
import { createStore } from 'vuex';

export default createStore({
    state() {
        return {
            cart: []
        };
    },
    mutations: {
        ADD_TO_CART(state, product) {
            state.cart.push(product);
        },
        REMOV
    },
    actions: {
        addToCart({ commit }, product) {
            commit('ADD_TO_CART', product);
        }
    },
    getters: {
        cartItems: state => state.cart
    }
});
