export default {
  name: 'ProductCatalog',
  data() {
    return {
      products: [],
      imagePaths: ['../assets/products/apple.jpg', '../assets/products/banana.jpg', '../assets/products/orange.jpg', '../assets/products/strawberry.jpg', '../assets/products/grape.jpg', '../assets/products/watermelon.jpg', '../assets/products/kiwi.jpg', '../assets/products/pineapple.jpg', '../assets/products/mango.jpg', '../assets/products/blueberry.jpg']
    };
  },
  methods: {
    async fetchProducts() {
      try {
        const response = await fetch('http://localhost:3000/products');
        if (!response.ok) {
          throw new Error(`HTTP error! status: ${response.status}`);
        }
        const products = await response.json();
        this.products = products;
      } catch (error) {
        console.error('Error fetching products:', error);
      }
    }
  },
  created() {
    this.fetchProducts();
  }
};
